const r=Array.from(document.querySelectorAll(".bottom-sheet")),n=new MutationObserver(function(e){e.forEach(t=>{const o=t.target;t.attributeName==="open"&&(o.hasAttribute("open")?document.documentElement.classList.add("overflow-hidden"):document.documentElement.classList.remove("overflow-hidden"))})});r.forEach(e=>{e.addEventListener("click",t=>{(t.target===e||t.target?.closest(".close-btn"))&&e.close()}),n.observe(e,{attributes:!0})});
